import { createStore } from "vuex";

export default createStore ({
  state: () => ({
    cards: [
      {
        _id: 1,
        title: 'Chick Corea',
        description: 'Children\'s Song No. 6',
      },
      {
        _id: 2,
        title: 'Pat Metheny',
        description: 'Antonia',
      },
      {
        _id: 3,
        title: 'Chick Corea',
        description: 'Children\'s Song No. 6',
      },
      {
        _id: 4,
        title: 'Pat Metheny',
        description: 'Antonia',
      },
      {
        _id: 5,
        title: 'Chick Corea',
        description: 'Children\'s Song No. 6',
      },
      {
        _id: 6,
        title: 'Pat Metheny',
        description: 'Antonia',
      },
      {
        _id: 7,
        title: 'Chick Corea',
        description: 'Children\'s Song No. 6',
      },
      {
        _id: 8,
        title: 'Pat Metheny',
        description: 'Antonia',
      },
    ],
    isDraggable: true,
  }),
  getters: {
    cards (state) {
      return state.posts;
    }
  },
  mutations: {
    setCards(state, cards) {
      state.cards = cards;
    },
    saveState(state) {
      localStorage.setItem('dashboard-cards', JSON.stringify(state.cards));
    },
    initializeStore(state) {
      try {
        const storedCards = JSON.parse(localStorage.getItem('dashboard-cards'));
        if (storedCards) {
          state.cards = storedCards;
        } else {
          localStorage.setItem('dashboard-cards', JSON.stringify(state.cards));
        }
      } catch (e) {
        localStorage.setItem('dashboard-cards', JSON.stringify(state.cards));
      }
    },
  },
  actions: {
    addCard ({state, commit}) {
      // return state.cards += 1;
    },
    removeCard ({state, commit}) {
      // return state.cards += 1;
    },
  },
  modules: {},
  plugins: [
    store => {
      store.subscribe((mutation, state) => {
        localStorage.setItem('dashboard-cards', JSON.stringify(state.cards));
      });
    }
  ]
})
