// import { defaulCardsArr } from '@/utils/constants.js'

export const cardsModule = {
  state: () => ({
    posts: [
      {
        _id: 1,
        title: 'Chick Corea',
        description: 'Children\'s Song No. 6',
      },
      {
        _id: 2,
        title: 'Pat Metheny',
        description: 'Antonia',
      },
    ],
    isPostsLoading: false,
    // selectedSort: '',
  }),
  getters: {
    posts (state) {
      return [...state.posts]
    },
  },
  mutations: {
    setPosts(state, posts) {
      state.posts = posts;
    },
    setIsPostsLoading(state, bool) {
      state.isPostsLoading = bool;
    },
    // setSelectedSort(state, selectedSort) {
    //   state.selectedSort = selectedSort;
    // },
  },
  actions: {
    fetchCards({state, commit}) {
      commit('setIsPostsLoading', true);
      // commit('setPosts', defaulCardsArr);
    },
  },
  modules: {},
  namespaced: true,
}
