import { createStore } from "vuex";

export default createStore ({
  state: () => ({
    cards: [
      {
        _id: 1,
        title: 'Chick Corea',
        description: 'Children\'s Song No. 6',
      },
      {
        _id: 2,
        title: 'Pat Metheny',
        description: 'Antonia',
      },
      {
        _id: 3,
        title: 'Chick Corea',
        description: 'Children\'s Song No. 6',
      },
      {
        _id: 4,
        title: 'Pat Metheny',
        description: 'Antonia',
      },
      {
        _id: 5,
        title: 'Chick Corea',
        description: 'Children\'s Song No. 6',
      },
      {
        _id: 6,
        title: 'Pat Metheny',
        description: 'Antonia',
      },
      {
        _id: 7,
        title: 'Chick Corea',
        description: 'Children\'s Song No. 6',
      },
      {
        _id: 8,
        title: 'Pat Metheny',
        description: 'Antonia',
      },
    ],
    isDraggable: false,
  }),
  getters: {
    cards (state) {
      return state.posts;
    }
  },
  mutations: {
    setCards(state, cards) {
      state.cards = cards;
    },
    initializeStore(state) {
      try {
        const storedState = JSON.parse(localStorage.getItem('dashboard-state'));
        if (storedState) {
          Object.keys(storedState).forEach(key => {
            state[key] = storedState[key];
          });
        } else {
          localStorage.setItem('dashboard-state', JSON.stringify(state));
        }
      } catch (e) {
        localStorage.setItem('dashboard-state', JSON.stringify(state));
      }
    },
    setDraggable(state, value) {
      state.isDraggable = value;
    }
  },
  actions: {
    addCard ({state, commit}) {
      // return state.cards += 1;
    },
    removeCard ({state, commit}) {
      // return state.cards += 1;
    },
    toggleDraggable({ commit }, value) {
      commit('setDraggable', value);
    },
  },
  modules: {},
  plugins: [
    store => {
      store.subscribe((mutation, state) => {
        localStorage.setItem('dashboard-state', JSON.stringify(state));
      });
    }
  ]
})
